public int sum(int x,int y) {      
		System.out.println(x+y);
		return x+y;
	}

public int sub(int x,int y) {      
	System.out.println(x-y);
	return x-y;
}

public int mult(int x,int y) {      
	System.out.println(x*y);
	return x*y;
}

public int div(int x,int y) {      
	System.out.println(x/y);
	return x/y;
}
	
